// checkOrder.then()

function initPromise(text) {
  return new Promise((resolve,reject)=>{
    this.setState({typedText: text})
      if (this.state.typedText === text) {
        resolve(this.state.typedText)
      } else {
        reject(this.state.typedText)
      }
  })
}

class TextInput extends Component {

  constructor(props) {
    super(props)
    this.state = {
      state: 'A',
      typedText: ''
    }
  }

  onClickHandler = () => this.props.onClickHandler
  styleHandler = () => this.props.styleHandler

  // onChangeHandler = (event) => {
  //   this.setState({typedText: event.target.value})
  //   console.log('typedText: ' + this.state.typedText);
  //   this.props.onChange(event.target.value)
  // }



  onChangeHandler = (event) => {
    console.log('typedText: ' + this.state.typedText);
    initPromise(event.target.value)
    initPromise(event.target.value)
    .then((value)=>{this.props.onChange(value)})
  }






  render() {
    return (
      <input onClick={this.onClickHandler()} style={this.styleHandler()} placeholder={this.props.placeholderHandler} value={this.state.typedText} onChange={this.onChangeHandler}></input>
    )
  }
}

export default TextInput;

