import CardContainer from './CardContainer';

class CardApp extends Component {
  constructor(props) {
    super(props)
    this.state = {}    
  }

  checkScrollDirection = () => {
    console.log('checking');
    let lastScroll
    let isScrollUp
    window.addEventListener("scroll", function (event) {
      let thisScroll = document.documentElement.scrollTop
        if (thisScroll > lastScroll) {
          lastScroll = thisScroll
          isScrollUp = false
          console.log('down')
          return isScrollUp
        } else {
          lastScroll = thisScroll
          console.log('up')
          isScrollUp = true
          return isScrollUp
        }

    })
  }

  render() {
    return <CardContainer onS></CardContainer>
  }

}

export default CardApp;
