import React, { Component } from 'react';
import CardContainer from './CardContainer';
import './CardApp.css'
import CreateRawCardDataList from './CreateRawCardDataList';
import ConvertToMobileCardDataList from './ConvertToMobileCardDataList'

let appWidth

class CardApp extends Component {
  constructor(props) {
    super(props)
    this.state = {
      containerWidth:document.getElementById('card-app').clientWidth,
      screenType:'',
      cardDataList:[],
      cardLocationList:[]
    }    
  }

  componentWillMount = () => {
    window.addEventListener("resize", this.updateWidth)
  }  

  componentDidMount = () => {
    this.updateWidth()
  }

  updateWidth = () => {
    appWidth = document.getElementById('card-app').clientWidth
    this.setState({containerWidth:appWidth,screenType:this.getScreenType()},()=>{this.createCardList()})
    console.log(this.state)
  }

  createCardList = () => {
    console.log(this.state.screenType)
    switch (this.state.screenType) {
      case 'mobile phone':
        this.adaptMobileScreen()
        break;
      case 'tablet':
      case 'laptop':
      case 'PC screens and bigger':
        this.adaptLaptopScreen()
        break;
      default:
        throw new Error('Scree Type Error')
    }   
  }

  adaptMobileScreen = () => {
    this.setState({cardDataList:ConvertToMobileCardDataList(CreateRawCardDataList())},this.setCardLocationList)    
  }

  adaptLaptopScreen = () => {
    this.setState({cardDataList:CreateRawCardDataList()})
  }

  getScreenType = () => {
    let width = this.state.containerWidth
    if (width <= 480) {
      console.log('mobile phone')
      return 'mobile phone'
    } else if (width <= 1024) {
      console.log('tablet')
      return 'tablet'
    } else if (width <=1600) {
      console.log('laptop')
      return 'laptop'
    } else {
      console.log('PC screens and bigger')
      return 'PC screens and bigger'
    }
  }

  setContainerRef = (element) => {
    this.containerElement = element
  }


  render() {
    return <CardContainer cardListData={this.state.cardDataList} refHandler={this.setContainerRef} screenType={this.state.screenType}></CardContainer>
  }

}

export default CardApp;
