import React, { Component } from 'react';
import CaculateBtn from './CaculateBtn'
import CaculatorMonitor from './CaculatorMonitor'



class Caculator extends Component {
  constructor(props) {
    super(props)
    this.state = {
      number1:0,
      number2:0,
      displayValue: 0,
      operator:0
    }
  }

  passKeyInParent = (value) => {
    console.log('parent: ' + value)
    this.refs.CaculatorMonitor.showKeyOnMonitor('1')
  }

  render() {
    return ( 
      <div>
        <li>Caculator:</li>
        <CaculatorMonitor ref="child" key="caculatorMonitor" text={this.state.displayValue}></CaculatorMonitor>
        <CaculateBtn parentMethod={(value) => this.passKeyInParent(value)}></CaculateBtn>
      </div> 
      )
  }
}

export default Caculator;
