import React, { Component } from 'react';
import FormalTextShow from './FormalTextShow'
import FormalTextInput from './FormalTextInput'

class FormalTextApp extends Component {
  constructor(props) {
    super(props)
    this.state = {
      text: ''
    }
  }

  cleanText = () => {
    this.inputRef.value = '.'
    this.state.text = '.'

  }

  inputRef = 'null'

  handleTextInputChange = (text) => {
    this.setState({text:text})
  }

  render() {
    return (
      [
      <li>ee</li>,
      <FormalTextShow text={this.state.text}/>,
      <FormalTextInput inputTextRef={(element)=>this.inputRef=element} initText="Put some text here!" onChange={this.handleTextInputChange} value={this.state.text}/>,
      <button onClick={this.cleanText()}>Clean</button>
      ]
    )
  }

}

export default FormalTextApp;
