    this.setState({cardListData:cardList},()=>console.log('state set! ' + this.state.cardListData))
    console.log(this.state.cardListData)
  }

  putBCardInACard = () => {
    let bCard1 = {}
    let bCard2 = {}
    bCard1 = {BCardAttr:{style:{backgroundColor:'#dd9999'}},BCardChild:this.putCCard1InBCard()}
    bCard2 = {BCardAttr:{style:{backgroundColor:'#dd99dd'}},BCardChild:this.putCCar2InBCard()}
    return [bCard1,bCard2]
  }

  putCCard1InBCard = () => {
    let cCard1 = {value:'cCard1'}
    let cCard2 = {value:'cCard2'} 
    return [cCard1,cCard2]
  }

  putCCar2InBCard = () => {
    let cCard1 = {value:'cCard1'}
    let cCard2 = {value:'cCard2'} 
    return [cCard1,cCard2]
  }
  return props
}

export default CreateAndGetCardList;
