import React, { Component } from 'react';

const FormalListInput = (props) => {
  return <input 
  type="text" 
  /* value={props.value}  */
  onKeyPress={
    (event)=> {
      if(event.target.value !== undefined && event.key === "Enter"){
        props.onKeyPress()
      } 
    }
  }
  onChange={(event)=>props.onChange(event.target.value)}
  placeholder={props.initText}
  />

}

export default FormalListInput;
