import React, { Component } from 'react';
import CardContainer from './CardContainer';
import './CardApp.css'

let appWidth

class CardApp extends Component {
  constructor(props) {
    super(props)
    this.state = {
      containerWidth:document.getElementById('card-app').clientWidth,
      screenType:''
    }    
  }

  componentWillMount = () => {
    window.addEventListener("resize", this.updateWidth)
  }  

  componentDidMount = () => {
    this.updateWidth()
  }

  updateWidth = () => {
    appWidth = document.getElementById('card-app').clientWidth
    this.setState({containerWidth:appWidth,screenType:this.getScreenType()},()=>{console.log(this.state.screenType)})
    console.log(this.state)
  }

  getScreenType = () => {
    let width = this.state.containerWidth
    if (width <= 480) {
      console.log('mobile phone')
      return 'mobile phone'
    } else if (width <= 1024) {
      console.log('tablet')
      return 'tablet'
    } else if (width <=1600) {
      console.log('laptop')
      return 'laptop'
    } else {
      console.log('PC screens and bigger')
      return 'PC screens and bigger'
    }
  }

  setContainerRef = (element) => {
    this.containerElement = element
  }


  render() {
    return <CardContainer refHandler={this.setContainerRef} screenType={this.state.screenType}></CardContainer>
  }

}

export default CardApp;
