import React, { Component } from 'react';
import CaculateBtn from './CaculateBtn'
import CaculatorMonitor from './CaculatorMonitor'



class Caculator extends Component {
  constructor(props) {
    super(props)
    this.state = {
      number1:0,
      number2:0,
      displayValue: 0,
      operator:0
    }
  }

  passKeyInParent = (value) => {
    this.handleCaculating(value)
  }

  handleCaculating = (value) => {
    switch(value) {
      case '+':
        this.setState({operator:value})
        this.setState({number2:this.state.number1})
        this.showOnMonitor(value)
        break
      case '-':
        this.setState({operator:value})
        this.setState({number2:this.state.number1})
        this.showOnMonitor(value)
        break
      case 'X':
        this.setState({operator:value})
        this.setState({number2:this.state.number1})
        this.showOnMonitor(value)
        break
      case '%':
        this.setState({operator:value})
        this.setState({number2:this.state.number1})
        this.showOnMonitor(value)
        break
      case 'c':
        this.setState({
          number1:0,
          number2:0,
          displayValue: 0,
          operator:''
        })
        this.showOnMonitor(0)
        break
      case '=':
        let result = this.caculate(this.state.number1,this.state.number2, this.state.operator)
        this.setState({
          displayValue:result
        })
        this.showOnMonitor(result)
        break
      default:
        this.setState({number1:value})
        console.log(this.state.number1);
        this.showOnMonitor(value)
    }
    console.log(this.state)
  }

  showOnMonitor = (value) =>  this.refs.child.showKeyOnMonitor(value)

  caculate = (number1,number2,operator) => {
    let result
    switch(operator) {
      case '+':
        result = (number1 + number2)
        return result
      case '-':
        result = (number1 - number2)
        return result
      case 'X':
        result = (number1 * number2)
        return result
      case '%':
        result = (number1 / number2)
        return result
      default:
        return
    }
  }

  render() {
    return ( 
      <div>
        <li>Caculator:</li>
        <CaculatorMonitor ref="child" key="caculatorMonitor"></CaculatorMonitor>
        <CaculateBtn parentMethod={(value) => this.passKeyInParent(value)}></CaculateBtn>
      </div> 
      )
  }
}

export default Caculator;
