import React, { Component } from 'react';
import FormalListInput from './FormalListInput'
import FormalListItem from './FormalListItem'

class FormalListApp extends Component {
  constructor(props) {
    super(props)
    this.state = {
      items:[],
      inputValue:''
    }
  }

  handleKeyPress = () => {}

  handleInputChange = (value) => {
    this.setState({inputValue:value})
  }

  handleItemDelete = (index) => {
    this.setState({items: [ ]})
  }

  render() {
    return (

      <div>
      <FormalListInput
      initInput="Put some text!"
      onKeyPress={this.handleKeyPress}
      onChange={this.handleInputChange}
      value={this.state.inputValue}
      ></FormalListInput>
      {this.state.items.map((value,index)=>{
        return <FormalListItem onDelete={()=>this.handleItemDelete(index)} key={index}>{this.value}</FormalListItem>
      })}
      </div>
    )
  }

}

export default FormalListApp;
