import React, { Component } from 'react';
import ToDoListPreviewInput from './ToDoListPreviewInput'
import ToDoListPreviewItem from './ToDoListPreviewItem'

class ToDoListPreviewApp extends Component {
  constructor(props) {
    super(props)
    this.state = {
      listItemArray:[],
      inputText:'',
      editItemIndex:''
    }
  }

  deleteListItem = (index) => {
    this.setState({listItemArray:this.state.listItemArray.filter((item) => item !== this.state.listItemArray[index])})
    console.log(index)
  }

  showListItem = () => {
    (this.state.editItemIndex === '')?
    this.state.listItemArray.map((value,index)=>{
      return <ToDoListPreviewItem itemText={value.text} deleteHandler={()=>this.deleteListItem(index)} key={index} editHandler={()=>this.editItemAction(index)}></ToDoListPreviewItem>
      console.log();
    })
    :
    this.state.listItemArray.map((value,index)=>{
      if (index === this.state.editItemIndex) {
        return <input />
      } else {
        return <ToDoListPreviewItem itemText={value.text} deleteHandler={()=>this.deleteListItem(index)} key={index} editHandler={()=>this.editItemAction(index)}></ToDoListPreviewItem>
      }
    })
  }

  onChangeAction = (event) => {
    this.setState({inputText:event.target.value})
  }

  handleOnClick = (event) => {
    this.state.listItemArray.push({text:this.state.inputText})
    console.log(this.state.listItemArray)
    this.setState({inputText:''})
    this.inputElement.value = ''
  }

  editItemAction = (index) => {
    console.log('editItemAction' + index)
    this.setState({editItemIndex: index})
  }

  render() {
    return (
      <div>
        <div></div>
        <button onClick={this.handleOnClick}>Add</button>
        <ToDoListPreviewInput 
        buttonRef={(element) => this.inputElement = element} 
        value={this.state.inputText} 
        onChangeHandler={this.onChangeAction}></ToDoListPreviewInput>
        {this.showListItem()}
      </div>
    )
  }

}

export default ToDoListPreviewApp;
