}

const convertCardList = (rawCardDataList) => {

  let mobileCardDataList = []

  rawCardDataList.map((aCardItem,aCardIndex)=>{

    mobileCardDataList.push({ACardAttr:aCardItem.ACardAttr,ACardChild:[]})

    console.log(aCardItem.ACardChild)
    console.log(mobileCardDataList[aCardIndex].ACardAttr)

    let mobileACardChild = mapBCardInACardItem(aCardItem.ACardChild,aCardIndex)

    mobileCardDataList[aCardIndex].ACardChild.push(mobileACardChild)
    console.log(mobileCardDataList[aCardIndex].ACardChild)

  })


}

