    let mobileBCardItem = mapCCardInBCardItem(bCardItem.BCardChild,bCardIndex)
    console.log(mobileBCardItem)

    aCardChild.push(mobileBCardItem)

  })

  console.log(aCardChild)

  return aCardChild

}

const mapCCardInBCardItem = (bCardItem,bCardIndex) => {

  let mobileACardChild = []

  bCardItem.map((cCardItem,cCardIndex)=>{
    console.log(cCardItem)
    let mobileBCardItem = {BCardAttr:bCardItem.bCardAttr,BCardChild:[cCardItem]}
    console.log(mobileBCardItem)
    mobileACardChild.push(mobileBCardItem)
  })

  return mobileACardChild

}

const putBCardInACard = () => {
