import React, { Component } from 'react';
import './App.css'

const styles = {
  textAlign:'center',
  color:'#9999ff'
}

class CountingMachine extends Component {

    constructor(props) {
      super(props)
      this.state = {
        totall: 0
      }

    }

    addButtonClickHandler = () => {
      this.setState({totall: this.state.totall + 1})
    }

    minorButtonClickHandler = () => {
      this.setState({totall: this.state.totall + 1})
    }

    render() {

      return (
      <div style={styles}>
        <h1 className="counter-layout">{this.state.totall}</h1>
        {/* 較不易維護的更改狀態的方法 */}
        {/* <button onClick={this.setState({totall: this.state.totall + 1})}>+</button> */}
        <button onClick={this.addButtonClickHandler}>+</button>
        <button onClick={this.minorButtonClickHandler}>-</button>
      </div>
      )
    }
}

export default CountingMachine;
