import React, { Component } from 'react';

function ConvertToMobileCardDataList(rawCardDataList) {
  return _convertCardList(rawCardDataList)
}

const _convertCardList = (rawCardDataList) => {
  let mobileCardDataList = []

  rawCardDataList.map((listChild,aCardIndex)=>{
    mobileCardDataList.push({ACardAttr:listChild.ACardAttr,ACardChild:[]})
    let mobileACardChild = _mapBCardInACardItem(listChild.ACardChild,aCardIndex)
    mobileCardDataList[aCardIndex].ACardChild.push(mobileACardChild)
    console.log(mobileCardDataList)
  })
}
// mapBCardInACardItem
const _mapBCardInACardItem = (aCardChild,aCardIndex) => {
  let mobileACardChild = []

  aCardChild.map((bCardItem,bCardIndex)=>{
    _mapCCardInBCardItem(bCardItem.BCardChild,bCardIndex,bCardItem,mobileACardChild)
  })

  return mobileACardChild
}

const _mapCCardInBCardItem = (bCardChild,bCardIndex,bCardItem,mobileACardChild) => {
  bCardChild.map((cCardItem,cCardIndex)=>{
    let mobileBCardItem = {BCardAttr:bCardItem.BCardAttr,BCardChild:[cCardItem]}
    mobileACardChild.push(mobileBCardItem)
  })
}

export default ConvertToMobileCardDataList;
