import TextShow from './TextShow'

class TextInput extends Component {

  constructor(props) {
    super(props)
    this.state = {
      text: '哈哈哈'
    }
  }

  renderTextInParent = () => {
    console.log('hahaha')
    return this.text()
  }

  text = () => 'text111'

  render() {
    return (
      <TextShow text={this.state.text}></TextShow>
    )
  }
}

export default TextInput;
