import React, { Component } from 'react';
import FormalListInput from './FormalListInput'
import FormalListItem from './FormalListItem'

class FormalListApp extends Component {
  constructor(props) {
    super(props)
    this.state = {
      items:[],
      inputValue:''
    }
  }
  render() {
    return (
      [
      <FormalListInput
      initInput="Put some text!"
      onKeyPress={this.handleKeyPress}
      onChange={this.handleInputChange}
      value={this.state.inputValue}
      ></FormalListInput>,
      {}
      ]
    )
  }

}

export default FormalListApp;
