import React, { Component } from 'react';
import FormalListInput from './FormalListInput'
import FormalListItem from './FormalListItem'

class FormalListApp extends Component {
  constructor(props) {
    super(props)
    this.state = {
      items:[],
      inputValue:'11'
    }
  }

  handleKeyPress = () => {
    const newItem = this.state.inputValue
    this.setState({items: [newItem, ...this.state.items],inputValue:''})
    console.log(this.state.items);

  }

  handleInputChange = (value) => {
    this.setState({inputValue:this.inputElement.value})
  }

  handleItemDelete = (id) => {
      const newItems = this.state.items.filter((value,index) => (index !== id))
      this.setState({items: newItems})
      console.log(id,this.state.items);

  }

  setRef = (element) => {
    this.inputElement = element
    console.log(this.inputElement);
  }


  render() {
    return (

      <div>
      <FormalListInput
      initInput="Put some text!"
      onKeyPress={this.handleKeyPress}
      onChange={this.handleInputChange}
      value={this.state.inputValue}
      inputRefSetting={this.setRef}
      ></FormalListInput>
      {this.state.items.map((value,index)=>{
        return <FormalListItem onDelete={()=>this.handleItemDelete(index)} key={index} value={this.state.items[index]}></FormalListItem>
      })}
      </div>
    )
  }

}

export default FormalListApp;
