import React, { Component } from 'react';
import TextShow from './TextShow'
import TextInput from './TextInput'

class TextApp extends Component {

  constructor(props) {
    super(props)
    this.state = {
      text: 'hahaha',
      placeHolderText: '打字吧！'
    }
  }

  text = () => 'text111'

//   getNewStyle = () => {
//     return {height:200+'px'}
//   }

//   getOnClickAction = () => {
//     console.log('you just clicked me')
// }

//   getPlaceholder = () => {
//     return 'hahaha'
//   }

  onClickAction = () => {
    console.log('you just clicked me')

  }


  render() {
    return (
      [
      <TextShow text={this.state.text}></TextShow>,
        <TextInput onClickHandler={this.onClickAction} styleHandler={this.setStyle}></TextInput>
      ]
    )
  }
}

export default TextApp;

