    function destructing(...[a,b,c]) {
      console.log(a+b+c);
    }
    destructing(1)
    destructing(1,2,3)
    destructing(1,2,3,4)

    function destructingOrigional(...parameter) {
      const [a,b,c] = parameter
      console.log(a+b+c);
    }
    destructingOrigional(1)
    destructingOrigional(1,2,3)
    destructingOrigional(1,2,3,4)

// 6-3 create An object

    const emptyObject = {}
