import React, { Component } from 'react';
import ToDoListPreviewInput from './ToDoListPreviewInput'
import ToDoListPreviewItem from './ToDoListPreviewItem'

class ToDoListPreviewApp extends Component {
  constructor(props) {
    super(props)
    this.state = {
      listItemArray:[],
      inputText:''
    }
  }

  deleteListItem = (event) => {
    this.setState({listItemArray:this.state.listItemArray.filter((item) => item !== this.state.listItemArray[0])})
    console.log(this.state.listItemArray)
    console.log('event target: ' + event.target);
    this.setState({listItemArray: this.state.listItemArray.filter((item) => item !== this.state.listItemArray[event.target.key])})
  }

  showListItem = () => {
    return this.state.listItemArray.map((value,index)=>{
      console.log('order: ' +this.state.listItemArray.indexOf(item));
      return <ToDoListPreviewItem itemText={item.text} deleteHandler={this.deleteListItem} key={this.state.listItemArray.indexOf(item)}></ToDoListPreviewItem>
    })
  }

  onChangeAction = (event) => {
    this.state.inputText = event.target.value
    console.log('changed!' + this.state.inputText);
  }

  handleOnClick = (event) => {
    this.state.listItemArray.push({text:this.state.inputText})
    this.setState({inputText:''})
    this.inputElement.value = ''
  }

  render() {
    return (
      <div>
        <div></div>
        <button onClick={this.handleOnClick}>Add</button>
        <ToDoListPreviewInput buttonRef={(element) => this.inputElement = element} value={this.state.inputText} onChangeHandler={this.onChangeAction}></ToDoListPreviewInput>
        {this.showListItem()}
      </div>
    )
  }

}

export default ToDoListPreviewApp;
