import React, { Component } from 'react';
import './App.css'

const styles = {
  textAlign:'center',
  color:'#9999ff'
}

class CountingMachine extends Component {

    constructor(props) {
      super(props)
      this.state = {
        totall: 0
      }
    }

    buttonClickHandler = (isMinorMode) => this.setState({totall: this.state.totall * 1 * (isMinorMode && -1)})

    render() {
      return (
      <div style={styles}>
        <h1 className="counter-layout">0</h1>
        {/* 較不易維護的更改狀態的方法 */}
        {/* <button onClick={this.setState({totall: this.state.totall + 1})}>+</button> */}
        <button onClick={this.buttonClickHandler(true)}>+</button>
        <button>-</button>
      </div>
      )
    }
}

export default CountingMachine;
