import React, { Component } from 'react';
import ACard from './ACard'
import BCard from './BCard'
import './CardContainer.css'

let lastSrollTime = 0
let currentDeltaY = undefined
// isScrollDirestionUp 0 is no scroll, 1 is scroll up, -1 is scroll down
let isScrollDirestionUp = 0

class CardContainer extends Component {

  componentWillMount = () => {
    this.preventScroll()
    this.createCardList()
  }

  componentDidMount = () => {
  }

  componentWillUnmount = () => {
  }

  constructor(props) {
    super(props)
    this.state = {
      transformY:0,
      animationTime:1200,
      quietPeriod:0,
      cardListData:[]
    }
  }

  preventScroll = () => {
    let containerClass = this
    window.addEventListener('wheel',function(event) {
      event.preventDefault()
      containerClass.stablizeScroll(event)
    })
  }

  stablizeScroll = (event) => {
    let timeNow = new Date().getTime();
    let enableScrolling = Boolean(timeNow - lastSrollTime < (this.state.quietPeriod + this.state.animationTime));
    if(!enableScrolling) {
      currentDeltaY = event.deltaY
      lastSrollTime = timeNow
      this.onStableScroll()
    }
  }

  onStableScroll = () => {
    isScrollDirestionUp = this.getIsDirectionUp()
    this.moveNextLocation()
    this.rawMove(isScrollDirestionUp)
  } 

  getIsDirectionUp = () => {
    return (currentDeltaY !== 0)? ( (currentDeltaY < 0)? -1 : 1 ) : 0
  }

  rawMove = (isScrollDirestionUp) => {
    console.log(this.containerElement)
    this.containerElement.scrollTop = this.containerElement.scrollTop+ 100
    console.log(this.containerElement.scrollTop);
    this.setState({transformY: this.state.transformY + (-100 * isScrollDirestionUp)})
  }

  moveNextLocation = () => {

  }

  refHandler = (element) => {
    console.log(element);
    this.containerElement = element
    this.props.refHandler()
  }

  createCardList = () => {
    let cardListData = []
    cardListData.push({ACardAttr:{style:{height:'300px'}},ACardChild:this.putBCardInACard()})
    console.log(cardListData)
    this.setState({cardListData:cardListData})
  }

  putBCardInACard = () => {
    let bCard1 = []
    bCard1.push({BCardAttr:{style:{height:'100px'}},BCardChild:this.putCCardInBCard()})
    return [bCard1]
  }

  putCCardInBCard = () => {
    let cCard1 = {value:'cCard1'}
    let cCard2 = {value:'cCard2'} 
    return [cCard1,cCard2]
  }

  render() {
    console.log('rerender!');
    let style = {transform:"translate3d(0px," + this.state.transformY + "vh,0px)"} 
    return (
    <div id="card-container" style={style} ref={this.refHandler}>
      <button value="scroll" onClick={this.rawMove}></button>
      hahahaaa
      {console.log('render')}
      {this.state.cardListData.map((aCardItem,aCardIndex,listArray) => {
        return <ACard key={aCardIndex} style={aCardItem.ACardAttr.style}>
          {aCardItem.ACardChild.map((bCardItem,bCardIndex,aCardArray)=>{
            console.log();

            return (<BCard key={bCardIndex} style={bCardItem.BCardAttr.style}>
              {bCardItem.BCardChild.map((cCardItem,cCardIndex,bCardArray)=>{
                return <div key={cCardIndex}>{cCardItem.value}</div>
              })}
              </BCard>)
          })}
        </ACard>
      })}
    </div>

    )
  }

}

export default CardContainer;
