import React, { Component } from 'react';
import TextShow from './TextShow'
import TextInput from './TextInput'

class TextApp extends Component {

  constructor(props) {
    super(props)
    this.state = {
      text: 'hahaha',
      placeHolderText: '打字吧！'
    }
  }

  renderTextInParent = () => {
    console.log('hahaha')
    return this.text
  }



  text = () => 'text111'

  placeHolder = (value) => this.state.placeHolderText + value

  render() {
    return (
      [
      <TextShow text={this.state.text}></TextShow>,
      <TextInput click placeHolder={this.placeHolder}></TextInput>
      ]
    )
  }
}

export default TextApp;


