import React, { Component } from 'react';
import CardContainer from './CardContainer';
import './CardApp.css'

let appWidth

class CardApp extends Component {
  constructor(props) {
    super(props)
    this.state = {
      containerWidth:document.getElementById('card-app').clientWidth,
      screenType:''
    }    
  }

  componentWillMount = () => {
    this.preventScroll()
    window.addEventListener("resize", this.updateWidth)
  }  

  componentDidMount = () => {
    this.updateWidth()
  }

  updateWidth = () => {
    appWidth = document.getElementById('card-app').clientWidth
    this.setState({containerWidth:appWidth})
    this.checkWidth()
    console.log(this.state.containerWidth)
  }

  preventScroll = () => {
    window.addEventListener('wheel',function(event) {
      console.log(event.deltaY)
      event.preventDefault();
    })
  }

  checkWidth = () => {
    let width = this.state.containerWidth
    if (width <= 480) {
      console.log('mobile phone')
    } else if (width <= 1024) {
      console.log('tablet')
    } else if (width <=1600) {
      console.log('laptop')
    } else {
      console.log('PC screens and bigger')
    }
  }

  setContainerRef = (element) => {
    this.containerElement = element
  }


  render() {
    return <CardContainer refHandler={this.setContainerRef}></CardContainer>
  }

}

export default CardApp;
