import React, { Component } from 'react';
import ACard from './ACard'
import './CardContainer.css'

let lastSrollTime = 0
let currentDeltaY = undefined
// isScrollDirestionUp 0 is no scroll, 1 is scroll up, -1 is scroll down
let isScrollDirestionUp = 0

class CardContainer extends Component {

  componentWillMount = () => {
    this.preventScroll()
  }

  componentDidMount = () => {
  }

  componentWillUnmount = () => {
  }

  constructor(props) {
    super(props)
    this.state = {
      transformY:0,
      animationTime:1200,
      quietPeriod:0,
      currentLocation:[0,0,0]
    }
  }

  preventScroll = () => {
    let containerClass = this
    window.addEventListener('wheel',function(event) {
      event.preventDefault()
      containerClass.stablizeScroll(event)
    })
  }

  stablizeScroll = (event) => {
    let timeNow = new Date().getTime();
    let enableScrolling = Boolean(timeNow - lastSrollTime < (this.state.quietPeriod + this.state.animationTime));
    if(!enableScrolling) {
      currentDeltaY = event.deltaY
      lastSrollTime = timeNow
      this.onStableScroll()
    }
  }

  onStableScroll = () => {
    isScrollDirestionUp = this.getIsDirectionUp()
    console.log(isScrollDirestionUp)
    this.moveNextLocation(isScrollDirestionUp)
    this.rawMove(isScrollDirestionUp)
  } 

  getIsDirectionUp = () => {
    return (currentDeltaY !== 0)? ( (currentDeltaY < 0)? 1 : -1 ) : 0
  }

  rawMove = (isUp) => {
    this.setState({transformY: this.state.transformY + (this.props.getBCardOuterHeight * isUp)})
    console.log(((isUp === 0)? '(raw move) no move' : (isUp < 0)? 'raw move up' : 'raw move down'));
  }

  getCurrentLocation = () => {
    return this.state.currentLocation
  }

  moveNextLocation = (isUp) => {
    let current = this.getCurrentLocation()
    // 假設的位置
    let assumedNext = this.getNextBCardLocation(current,isUp)
    // this.setState({currentLocation:assumedNext},()=>{console.log('location updated: ' + this.state.currentLocation)})
    // 確認假設的位置
    let nextType = this.getNextType(current,assumedNext)

    switch (nextType) {
      case 'switch B-Card':
        this.setState({currentLocation:this.getNextBCardLocation(current,isUp)})
        break;
      case 'switch A-Card':
        this.setState({currentLocation:this.getNextACardLocation(current,isUp)})
        break;  
      case 'reach end':
      break;  
      default:
        throw new Error('new card location not exists!')
    }

    // 以正確的位置執行
  }

  getNextBCardLocation = (currentLocation,isUp) => {
    let newLocation = [currentLocation[0], currentLocation[1] + (1 * isUp), currentLocation[2]]
    return newLocation
  }

  getNextACardLocation = (currentLocation,isUp) => {
    let checkLastOfFirst = (isUp) => {
      console.log(isUp)      
      if (isUp === -1) {
        console.log(0)
        return 0
      } else if (isUp === 1) {
        console.log(this.props.getCardLocationList[currentLocation[0]].length)
        console.log(this.props.getCardLocationList[currentLocation[0]])
        return currentLocation[0].length
      } else {
        throw new Error("It dosen't move!")
      }
    }
    let newLocation = [currentLocation[0] + (1 * isUp), checkLastOfFirst(isUp), currentLocation[2]]
    return newLocation
  }

  getNextType = (current,assumedNext) => {
    console.log(this.props.getCardLocationList);
    console.log(assumedNext[0],assumedNext[1],assumedNext[2]);

    // check type

    if (this.props.getCardLocationList[assumedNext[0]] === undefined) {
      // A頂
      if (this.props.getCardLocationList[assumedNext[0]][assumedNext[1]] === undefined) {
        // A頂也B頂 - 到底了或不存在
        console.log(`剛剛: ${current}, 不確定的現在: ${assumedNext} 到底了`)       
        return 'reach end' 
      } else {
        // A頂B不頂 - 最後一張A-card
        console.log(`剛剛: ${current}, 不確定的現在: ${assumedNext}不該存在`) 
        return 'switch B-Card'
      }
    } else  {
      // A不頂
      if (this.props.getCardLocationList[assumedNext[0]][assumedNext[1]] === undefined) {
        // A不頂B頂 - 換A卡
        console.log(`剛剛: ${current}, 不確定的現在: ${assumedNext}換A`) 
        return 'switch A-Card'
      } else {
        // A不頂B不頂 - 換B卡
        console.log(`剛剛: ${current}, 不確定的現在: ${assumedNext}換B`) 
        return 'switch B-Card'
      }
    }




  }

  refHandler = (element) => {
    this.containerElement = element
    this.props.refHandler()
  }


  render() {
    let style = {transform:"translate3d(0px," + this.state.transformY + "vh,0px)"} 
    return (
    <div id="card-container" style={style} ref={this.refHandler}>
      {    
        this.props.cardListData.map((aCardItem,aCardIndex,listArray)=>{
          return <ACard key={aCardIndex} child={aCardItem.ACardChild} style={aCardItem.ACardAttr.style}></ACard>
        })
      }
    </div>

    )
  }

}

export default CardContainer;
