import React, { Component } from 'react';
import ACard from './ACard'
import './CardContainer.css'

let lastSrollTime = 0
let currentDeltaY = undefined
// isScrollDirestionUp 0 is no scroll, 1 is scroll up, -1 is scroll down
let isScrollDirestionUp = 0

class CardContainer extends Component {

  componentWillMount = () => {
    this.preventScroll()
  }

  componentDidMount = () => {    
  }

  componentWillUnmount = () => {
  }

  constructor(props) {
    super(props)
    this.state = {
      transformY:0,
      animationTime:1200,
      quietPeriod:0,
      currentLocation:[0,0,0],
      cardActiveStatus: undefined
    }
  }

  setUpCardActiveStatus = (cardLocationList) => {
    let cardActiveStatus = cardLocationList.map((ACardLocation)=>{
      if (ACardLocation[0] !== undefined ) {return 0} else {throw new Error('A A-Card has no child!')}
    })
    this.setState({cardActiveStatus:cardActiveStatus},() => {
      this.updateCardActiveStatus(cardActiveStatus,this.getCurrentLocation())  
    })
  }

  updateCardActiveStatus = (cardActiveStatus,toActivateLocation) => {
    let newStatus = cardActiveStatus
    newStatus[toActivateLocation[0]] = toActivateLocation[1]
    this.setState({cardActiveStatus:newStatus},()=>{
      console.log('car active status updated: '+this.state.cardActiveStatus)
    })
  }

  preventScroll = () => {
    let containerClass = this
    window.addEventListener('wheel',function(event) {
      event.preventDefault()
      containerClass.stablizeScroll(event)
    })
  }

  stablizeScroll = (event) => {
    let timeNow = new Date().getTime();
    let enableScrolling = Boolean(timeNow - lastSrollTime < (this.state.quietPeriod + this.state.animationTime));
    if(!enableScrolling) {
      currentDeltaY = event.deltaY
      lastSrollTime = timeNow
      this.onStableScroll()
    }
  }

  onStableScroll = () => {
    isScrollDirestionUp = this.getIsDirectionUp()
    console.log(isScrollDirestionUp)
    this.moveNextLocation(isScrollDirestionUp)
  } 

  getIsDirectionUp = () => {
    return (currentDeltaY !== 0)? ( (currentDeltaY < 0)? 1 : -1 ) : 0
  }

  rawMoveNext = (isUp) => {
    this.setState({transformY: this.state.transformY + (this.props.getBCardOuterHeight * isUp)})
    console.log(((isUp === 0)? '(raw move) no move' : (isUp < 0)? 'raw move down' : 'raw move up'));
    if (this.state.cardActiveStatus === undefined) {
      this.setUpCardActiveStatus(this.props.getCardLocationList)
    } else {

      this.updateCardActiveStatus(this.state.cardActiveStatus,this.getCurrentLocation())
    }
  }

  getCurrentLocation = () => {
    return this.state.currentLocation
  }

  moveNextLocation = (isUp) => {
    let isDown = isUp * -1
    let current = this.getCurrentLocation()
    console.log('current: ' + [current[0],current[1]]);

    // 確認上/下一張B卡存在並設定當前位置
    if (this.isLocationExists([current[0],(current[1] + isDown)])) {
      console.log('上/下一張B卡存在 ')
      // 上/下一張B卡存在 
      this.setState({currentLocation:[current[0],(current[1] + isDown),current[2]]})
      // 上/下一張B卡不存在，確認往上或往下
    } else if (isUp === 1 && this.isLocationExists([(current[0] + isDown),0])) {
      console.log('上一張B卡不存在，若往上且上一A張卡存在，切上一張A卡並移到最後一張B卡')
      // 上一張B卡不存在，若往上且上一A張卡存在，切上一張A卡並移到最後一張B卡
      this.setState({currentLocation:[(current[0] + isDown),(this.props.getCardLocationList[current[0]+isDown].length-1),0]})
    } else if (isUp === -1 && this.isLocationExists([(current[0] + isDown),0])) {
      console.log('下一張B卡不存在，若往下且下一A張卡存在，切下一張A卡並移到第0張B卡')
      // 下一張B卡不存在，若往下且下一A張卡存在，切下一張A卡並移到第0張B卡
      this.setState({currentLocation:[(current[0] + isDown),0,0]})
    } else {
      // 上/下一張B卡不存在，上/下一A張卡不存在
      if (current[0]===0&&current[1]===0&&isUp===1) {
        // 意圖往上，且已是第一張卡
        console.log('you reach the top!')
      } else if (current[0]===this.props.getCardLocationList.length-1 && current[1]===this.props.getCardLocationList[this.props.getCardLocationList.length-1].length-1&&isUp===-1) {
        // 意圖往下，且已是最後一張卡
        console.log('you reach the bottom!')
      } else {
        throw new Error('moveNextLocation: you may encounter a logic problem')
      }
    }

    this.rawMoveNext(isUp)
    this.jumpACardsInContainer(this.getCurrentLocation[0],1)
  }

  jumpACardsInContainer = (forACardLocation,toACardLocation) => {
    let distance = toACardLocation - forACardLocation
    this.setState({transformY: this.state.transformY + (this.props.getBCardOuterHeight * distance)})
  }

  moveACardInContainer = (forLocation,toLocation) => {

  }

  isLocationExists = (assumedNext) => {
    try {
      if (this.props.getCardLocationList[assumedNext[0]][assumedNext[1]] === undefined) {
        console.log([assumedNext[0],assumedNext[1]] + 'not exists! ')        
        // not exists
        return false
      } else if  (this.props.getCardLocationList[assumedNext[0]][assumedNext[1]] !== undefined) {
        console.log([assumedNext[0],assumedNext[1]] + 'exists! ')
        // exists
        return true
      } else {
        throw new Error('isExists has an exception')
      }
    } catch (error) {
      return false
    }
  }

  refHandler = (element) => {
    this.containerElement = element
    this.props.refHandler()
  }


  render() {
    let style = {transform:"translate3d(0px," + this.state.transformY + "vh,0px)"} 
    return (
    <div id="card-container" style={style} ref={this.refHandler}>
      {    
        this.props.cardListData.map((aCardItem,aCardIndex)=>{
          return <ACard key={aCardIndex} child={aCardItem.ACardChild} style={aCardItem.ACardAttr.style}></ACard>
        })
      }
    </div>

    )
  }

}

export default CardContainer;
