import React, { Component } from 'react';
import ACard from './ACard'
import './CardContainer.css'

let lastSrollTime = 0
let currentDeltaY = undefined
// isScrollDirestionUp 0 is no scroll, 1 is scroll up, -1 is scroll down
let isScrollDirestionUp = 0

class CardContainer extends Component {

  componentWillMount = () => {
    this.preventScroll()
  }

  componentDidMount = () => {
  }

  componentWillUnmount = () => {
  }

  constructor(props) {
    super(props)
    this.state = {
      transformY:0,
      animationTime:1200,
      quietPeriod:0,
      currentLocation:[0,0,0]
    }
  }

  preventScroll = () => {
    let containerClass = this
    window.addEventListener('wheel',function(event) {
      event.preventDefault()
      containerClass.stablizeScroll(event)
    })
  }

  stablizeScroll = (event) => {
    let timeNow = new Date().getTime();
    let enableScrolling = Boolean(timeNow - lastSrollTime < (this.state.quietPeriod + this.state.animationTime));
    if(!enableScrolling) {
      currentDeltaY = event.deltaY
      lastSrollTime = timeNow
      this.onStableScroll()
    }
  }

  onStableScroll = () => {
    isScrollDirestionUp = this.getIsDirectionUp()
    this.moveNextLocation(isScrollDirestionUp)
    this.rawMove(isScrollDirestionUp)
  } 

  getIsDirectionUp = () => {
    return (currentDeltaY !== 0)? ( (currentDeltaY < 0)? -1 : 1 ) : 0
  }

  rawMove = (isUp) => {
    this.setState({transformY: this.state.transformY + (-this.props.getBCardOuterHeight * isUp)})
    console.log(((isUp === 0)? '(raw move) no move' : (isUp < 0)? 'raw move up' : 'raw move down'));
  }

  getCurrentLocation = () => {
    return this.state.currentLocation
  }

  moveNextLocation = (isUp) => {
    let current = this.getCurrentLocation()
    let unCheckedNext = this.getNextLocation(current,isUp)
    this.getMoveType(current,unCheckedNext)
  }

  getNextLocation = (currentLocation,isUp) => {
    console.log();

    let newLocation = [currentLocation[0], currentLocation[1] + (1 * isUp), currentLocation[2]]
    console.log(newLocation)
    return newLocation
  }

  getMoveType = (current,unCheckedNext) => {
    console.log(this.props.getCardLocationList);
    console.log(unCheckedNext[0],unCheckedNext[1],unCheckedNext[2]);

    // // check exists
    // if (this.props.getCardLocationList[unCheckedNext[0]] !== undefined) {
    //   // if not, it is the last or first card
    //   if (this.props.getCardLocationList[unCheckedNext[0]][unCheckedNext[1]] !== undefined) {
    //     // if notm it is the last of first B-card in a A-card


    //   }
    // }

    //  check exists
    if (this.props.getCardLocationList[unCheckedNext[0]][unCheckedNext[1]] === undefined) {
      // B末端A不一定
      if (this.props.getCardLocationList[unCheckedNext[0]] === undefined) {
        // B末端也A末端 - 到底了
        console.log('B末端也A末端');
        console.log(`剛剛: ${current}, 不確定的現在: ${unCheckedNext}, 未來:`)
      } else  {
        // B末端A不末端 - 切換A卡
        console.log('B末端A不末端');
        console.log(`剛剛: ${current}, 不確定的現在: ${unCheckedNext}, 未來:`)
      }
    } else {
      //  A卡內部中的B捲動 - 切換B卡
      console.log('B末端也A末端');
      console.log(`剛剛: ${current}, 不確定的現在: ${unCheckedNext}, 未來:`)
    }
    this.setState({currentLocation:[unCheckedNext]})

  }

  refHandler = (element) => {
    this.containerElement = element
    this.props.refHandler()
  }


  render() {
    let style = {transform:"translate3d(0px," + this.state.transformY + "vh,0px)"} 
    return (
    <div id="card-container" style={style} ref={this.refHandler}>
      {    
        this.props.cardListData.map((aCardItem,aCardIndex,listArray)=>{
          return <ACard key={aCardIndex} child={aCardItem.ACardChild} style={aCardItem.ACardAttr.style}></ACard>
        })
      }
    </div>

    )
  }

}

export default CardContainer;
