import React, { Component } from 'react';


class HelloWorld extends Component {



  render() {

    // 運用陣列的資料建立很多標籤

    const toDo = [{id:'number1',text:'im todo1',key:'key1'},{id:'number2',text:'todo2',key:'key2'},{id:'number3',text:'todododo3',key:'key3'}]
    const todoList = toDo.map((toDoItem)=>{
      return (
        <li id={toDoItem.id} key={toDoItem.key}>{toDoItem.text}</li>
      )
    })

    // return 一個fragment的片段

    // const Fragment = React.Fragment
    // return (
    //   <Fragment>
    //     'some text'
    //     <div>123</div>
    //     <button>button</button>
    //   </Fragment>
    // )

    // 花括號表達樣式

    return (
      <div>

        <div class={123 + 456}></div>

        <div class={true}></div>

        <div class={{name:'a Object', age: 12}}></div>

        <div style={{color:'red'}}>red color</div>

        <div></div>

      </div>
    )


    return (
      <div>
        <button onClick={()=>alert('Thank you!')}>Click Me!</button>
        {todoList}          
      </div>
    ) 

  }
}

export default HelloWorld;
